import { environment } from '../../environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class MissionService {
    constructor(http) {
        this.http = http;
    }
    // CRUD Missões
    async createMission(mission) {
        try {
            const response = await this.http.post(`${environment.backendUrl}/missions`, mission).toPromise();
            return response.id;
        }
        catch (error) {
            console.error('Erro ao criar missão:', error);
            throw error;
        }
    }
    async getMission(id) {
        try {
            const mission = await this.http.get(`${environment.backendUrl}/missions/${id}`).toPromise();
            return mission || null;
        }
        catch (error) {
            console.error('Erro ao buscar missão:', error);
            return null;
        }
    }
    async getAllMissions() {
        try {
            const missions = await this.http.get(`${environment.backendUrl}/missions`).toPromise();
            return missions || [];
        }
        catch (error) {
            console.error('Erro ao buscar missões:', error);
            return [];
        }
    }
    async getActiveMissions() {
        try {
            const missions = await this.http.get(`${environment.backendUrl}/missions/active`).toPromise();
            return missions || [];
        }
        catch (error) {
            console.error('Erro ao buscar missões ativas:', error);
            return [];
        }
    }
    async updateMission(id, mission) {
        try {
            if (!id || typeof id !== 'string' || id.trim() === '' || id.includes('/')) {
                console.error('Invalid mission id provided to updateMission:', id);
                throw new Error('Invalid mission id provided to updateMission');
            }
            await this.http.put(`${environment.backendUrl}/missions/${id}`, mission).toPromise();
        }
        catch (error) {
            console.error('Erro ao atualizar missão:', error);
            throw error;
        }
    }
    async deleteMission(id) {
        try {
            if (!id || typeof id !== 'string' || id.trim() === '' || id.includes('/')) {
                console.error('Invalid mission id provided to deleteMission:', id);
                throw new Error('Invalid mission id provided to deleteMission');
            }
            await this.http.delete(`${environment.backendUrl}/missions/${id}`).toPromise();
        }
        catch (error) {
            console.error('Erro ao deletar missão:', error);
            throw error;
        }
    }
    // UserMissions
    async getUserMissions(userId) {
        try {
            const userMissions = await this.http.get(`${environment.backendUrl}/missions/user/${userId}`).toPromise();
            return userMissions || [];
        }
        catch (error) {
            console.error('Erro ao buscar missões do usuário:', error);
            return [];
        }
    }
    async startMission(userId, missionId) {
        try {
            const response = await this.http.post(`${environment.backendUrl}/missions/user/${userId}/start/${missionId}`, {}).toPromise();
            return response.id;
        }
        catch (error) {
            console.error('Erro ao iniciar missão:', error);
            throw error;
        }
    }
    async claimMission(userMissionId) {
        try {
            const docRef = doc(db, 'userMissions', userMissionId);
            await updateDoc(docRef, { claimed: true, claimedAt: Timestamp.now() });
        }
        catch (error) {
            console.error('Erro ao marcar missão como coletada:', error);
            throw error;
        }
    }
    async claimDaily(userMissionId, day) {
        try {
            const userMissionRef = doc(db, 'userMissions', userMissionId);
            await runTransaction(db, async (tx) => {
                const umSnap = await tx.get(userMissionRef);
                if (!umSnap.exists())
                    throw new Error('UserMission não encontrado');
                const umData = umSnap.data();
                const missionId = umData.missionId;
                const mission = await this.getMission(missionId || '');
                const totalDays = (mission && mission.dailyRewards && mission.dailyRewards.length) || 7;
                const claimedDays = (umData.claimedDays || []).slice();
                if (claimedDays.includes(day))
                    throw new Error('Dia já coletado');
                // ensure day is the next available unclaimed day (sequential rule)
                const allDays = Array.from({ length: totalDays }, (_, i) => i + 1);
                const nextUnclaimed = allDays.find(d => !claimedDays.includes(d));
                if (nextUnclaimed !== day)
                    throw new Error('Dia não disponível para coleta');
                // check time-based availability
                const nextAvailableTs = umData.nextAvailableAt;
                if (nextAvailableTs) {
                    const nextAvailableMillis = (nextAvailableTs.seconds ? nextAvailableTs.seconds * 1000 : nextAvailableTs.toMillis());
                    if (Date.now() < nextAvailableMillis)
                        throw new Error('Dia ainda não disponível');
                }
                claimedDays.push(day);
                const progress = Math.round((claimedDays.length / totalDays) * 100);
                const updates = { claimedDays, progress };
                if (claimedDays.length === totalDays) {
                    updates.completed = true;
                    updates.completedAt = Timestamp.now();
                    updates.claimed = true;
                    updates.claimedAt = Timestamp.now();
                    updates.nextAvailableAt = undefined;
                }
                else {
                    // set next available to +24h
                    updates.nextAvailableAt = Timestamp.fromMillis(Date.now() + 24 * 60 * 60 * 1000);
                }
                tx.update(userMissionRef, updates);
            });
        }
        catch (error) {
            console.error('Erro ao coletar dia:', error);
            throw error;
        }
    }
    async updateProgress(userMissionId, progress) {
        try {
            const docRef = doc(db, 'userMissions', userMissionId);
            await updateDoc(docRef, { progress });
        }
        catch (error) {
            console.error('Erro ao atualizar progresso:', error);
            throw error;
        }
    }
    async completeMission(userMissionId) {
        try {
            const docRef = doc(db, 'userMissions', userMissionId);
            await updateDoc(docRef, {
                completed: true,
                completedAt: Timestamp.now()
            });
        }
        catch (error) {
            console.error('Erro ao completar missão:', error);
            throw error;
        }
    }
}
