import { environment } from '../../environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class BoxService {
    constructor(http) {
        this.http = http;
    }
    async createBox(box) {
        const result = await this.http.post(`${environment.backendUrl}/boxes`, box).toPromise();
        return result.id;
    }
    async getActiveBoxes() {
        return await this.http.get(`${environment.backendUrl}/boxes/active`).toPromise();
    }
    async getBoxesByType(type) {
        return await this.http.get(`${environment.backendUrl}/boxes/by-type/${type}`).toPromise();
    }
    async getAllBoxes() {
        return await this.http.get(`${environment.backendUrl}/boxes`).toPromise();
    }
    async getBoxById(boxId) {
        const data = await this.http.get(`${environment.backendUrl}/boxes/${boxId}`).toPromise();
        return data ? Object.assign({ id: boxId }, data) : null;
    }
    async updateBox(boxId, data) {
        await this.http.put(`${environment.backendUrl}/boxes/${boxId}`, data).toPromise();
    }
    async deleteBox(boxId) {
        await this.http.delete(`${environment.backendUrl}/boxes/${boxId}`).toPromise();
    }
}
