import { environment } from '../../environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class TradeService {
    constructor(http) {
        this.http = http;
    }
    async createTrade(trade) {
        const response = await this.http.post(`${environment.backendUrl}/trades`, trade).toPromise();
        return response.id;
    }
    async getTradeById(tradeId) {
        const trade = await this.http.get(`${environment.backendUrl}/trades/${tradeId}`).toPromise();
        return trade || null;
    }
    async getUserSentTrades(userId) {
        const trades = await this.http.get(`${environment.backendUrl}/trades/user/${userId}/sent`).toPromise();
        return trades || [];
    }
    async getUserReceivedTrades(userId) {
        const trades = await this.http.get(`${environment.backendUrl}/trades/user/${userId}/received`).toPromise();
        return trades || [];
    }
    async acceptTrade(tradeId) {
        await this.http.put(`${environment.backendUrl}/trades/${tradeId}/accept`, {}).toPromise();
    }
    async rejectTrade(tradeId) {
        const docRef = doc(db, 'trades', tradeId);
        await updateDoc(docRef, {
            status: 'REJECTED',
            updatedAt: new Date()
        });
    }
    async cancelTrade(tradeId) {
        const docRef = doc(db, 'trades', tradeId);
        await updateDoc(docRef, {
            status: 'CANCELLED',
            updatedAt: new Date()
        });
    }
    async deleteTrade(tradeId) {
        await deleteDoc(doc(db, 'trades', tradeId));
    }
}
