import { environment } from '../../environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "./user.service";
import * as i3 from "./item.service";
export class RankingService {
    constructor(http, userService, itemService) {
        this.http = http;
        this.userService = userService;
        this.itemService = itemService;
    }
    async getGlobalRanking(limitCount = 50) {
        try {
            const rankingEntries = await this.http.get(`${environment.backendUrl}/rankings/global/${limitCount}`).toPromise();
            return rankingEntries || [];
        }
        catch (error) {
            console.error('Erro ao buscar ranking:', error);
            return [];
        }
    }
    async getRankingByBox(boxId, limitCount = 20) {
        try {
            const rankingEntries = await this.http.get(`${environment.backendUrl}/rankings/box/${boxId}/${limitCount}`).toPromise();
            return rankingEntries || [];
        }
        catch (error) {
            console.error('Erro ao buscar ranking por caixa:', error);
            return [];
        }
    }
}
